import Component from './Component';
import SceneManager from '../manager/SceneManager';
import RenderManager from '../manager/RenderManager';
import ProgramManager from '../manager/ProgramManager';
import Matrix3 from '../render/WebGL/Matrix3';
import Color from '../internal/Color';

const DepthRange = 10000000;

/**
 * Base Renderable Component for all Components that want to draw to the screen. If
 * you want to show something on screen, it should derive Renderable.
 * 
 * Contained in the class are functions for attaching the Renderable to viewports,
 * and updating Transform data into matricies for use with the Render Manager.
 * 
 * The Renderable Component contains data on how the Renderable expects to be rendered.
 * Programs, render vectors, shader data, and other data that is on a per-object basis
 * for rendering is defined here.
 * 
 * Classes which derive a Renderable should handle all base data and add or modify data
 * as needed to achieve certain effects.
 */
export default class Renderable extends Component {
    /**
     * Constructor for the Renderable component. Allows defaults to be assigned without
     * having to call setter functions later.
     * 
     * @param {Program} program A Program Object from the ProgramManager.
     */
    constructor(program = ProgramManager.getProgram('Default')) {
        super(true);
        this.deregisterViewports = {};

        //currently unused
        this.program = program;
        this._matrixPosition = new Matrix3();
        this._matrixScale = new Matrix3();
        this._matrixRotation = new Matrix3();
        this._matrixOriginOffset = new Matrix3();
        this.color = new Color();
        this.renderPositions = [0, 0, 1, 0, 0, 1, 0, 1, 1, 0, 1, 1];
        this.primitiveType = RenderManager.GL.TRIANGLES;
        this.primitiveCount = 6;

        //textures
        this.textures = [];
        this._subSpriteData = [0, 0, 1, 1];

        this.depth = 0.5;
    }

    requestRedraw() {
        RenderManager.forceUpdate();
    }

    setColor(r, g, b, a) {
        this.color.set(r, g, b, a);
        this.requestRedraw();
    }

    setDepth(depth) {
        this.depth = (Math.max(-DepthRange, Math.min(depth, DepthRange)) + DepthRange) / (DepthRange * 2);
        this.requestRedraw();
    }

    setSubIndex(spriteIndex) {
        if (this.textures.length < 1)
            return;

        let framesWidth = this.textures[0].frameWidth / this.textures[0].width;
        let framesHeight = this.textures[0].frameHeight / this.textures[0].height;

        let frameWidthIndex = spriteIndex % (1 / framesWidth);
        let frameHeightIndex = Math.floor(spriteIndex * framesWidth);

        this._subSpriteData = [-frameWidthIndex, -frameHeightIndex, framesWidth, framesHeight];
        this.requestRedraw();
    }

    setUniformData(positionMatrix) {
        this.program.setUniforms({
            'u_color': this.color.color,
            'u_matrix': positionMatrix,
            'u_depth': this.depth,
            'u_texture': 0,
            'u_subTexcoord': this._subSpriteData,
        });
        return true;
    }

    setTexture(textureObject) {
        this.textures[0] = textureObject;
        this.requestRedraw();
    }

    /**
     * Returns a joined matrix of position, scale, rotation and origin adjustment.
     */
    getMatrix() {       
        return Matrix3.copy(this._matrixPosition).multiply(this._matrixRotation).multiply(this._matrixScale).multiply(this._matrixOriginOffset);
    }

    /**
     * Sets the position matrix to the new position point assigned.
     * 
     * @param {Point} point A position point.
     */
    setPosition(point) {
        this._matrixPosition.setPosition(point.x, point.y); //point.z if we had 3D to override this
        this.requestRedraw();
    }

    /**
     * Sets the origin offset matrix to the new position point assigned. This matrix reacts differently from other matrix
     * assignment. 0,0 is regular top left corner origin, -0.5, -0.5 centers the origin to the center of the Transform, and
     * -1, -1 will align the origin to the bottom right.
     * 
     * @param {Point} point Origin offset point.
     */
    setOriginOffset(point) {
        this._matrixOriginOffset.setPosition(point.x, point.y); //point.z if we had 3D to override this
    }

    /**
     * Sets the rotation matrix to the new rotation value assigned.
     * 
     * TODO: Change to a rotation point for x, y, z rotation instead of just z.
     * 
     * @param {number} rotation A rotation in degrees.
     */
    setRotation(rotation) {
        this._matrixRotation.setRotation(rotation);
        this.requestRedraw();
    }

    /**
     * Sets the scale matrix to the new scale point assigned.
     * 
     * @param {Point} scale A scale point.
     */
    setScale(scale) {
        this._matrixScale.setScale(scale.x, scale.y);
        this.requestRedraw();
    }

    /**
     * Called immediatly after the Render component is added to a game object.
     * 
     * Updates the data of this renderable to the current Transform data.
     */
    onAddComponent() {
        let transform = this.gameObject.getComponent("Transform");
        transform.renderable = this;
        this.setPosition(transform._position);
        this.setScale(transform._scale);
        this.setRotation(transform._rotation);
        this.requestRedraw();
    }

    /**
     * Adds this component to another viewport to be drawn. 
     * 
     * @param {number} viewportID Object ID of the viewport.
     */
    addToViewport(viewportID) {
        this.deregisterViewports[viewportID] = SceneManager.getCurrentScene().registerRenderableComponent(this, viewportID);
        this.requestRedraw();
        return this;
    }

    /**
     * Removes the renderable from all viewports.
     */
    removeFromViewports() {
        let objKeys = Object.keys(this.deregisterViewports);
        for (let i = 0; i < objKeys.length; i++) {
            this.deregisterViewports[objKeys[i]]();
        }
        this.requestRedraw();
    }

    onEnd() {
        this.removeFromViewports();
    }
}

import Renderable from './Renderable';
import Matrix3 from '../render/WebGL/Matrix3';

/**
 * Renderable2D is a renderable component which focusses on
 * 2D shaders and matricies for 2D space rendering.
 */
export default class Renderable2D extends Renderable {
    constructor() {
        super();
        this.className = 'Renderable2D';
    }
}

import Renderable from './Renderable';
import Matrix3 from '../render/WebGL/Matrix3';
import ProgramManager from '../manager/ProgramManager';
import TextureManager from '../manager/TextureManager';
import RenderManager from '../manager/RenderManager';
import Renderable2DMultitex from './Renderable2DMultitex';

var dataTexCounter = 0;

/**
 * Renderable2DGrid is a renderable component which uses two textures to draw
 * tilemaps using the GPU, and just one renderable. Renderable2DGrid will expect one
 * texture for sprites, and another containing the data for which tile goes where.
 * Additionaly, viewport support is added to make large maps require one time loads into self
 * managed data textures.
 */
export default class Renderable2DGrid extends Renderable2DMultitex {
    /**
     * Constructor for Renderable2D grid. Sets up data and initializes a data texture.
     * 
     * @param {string} mapTilesTextureName Name of sprite sheet texture.
     * @param {Array} gridData Array of indexes for each tile on the map.
     * @param {number} width Width of the map.
     * @param {number} height Height of the map.
     * @param {number} tileViewWidth Width of the viewport.
     * @param {number} tileViewHeight Height of the viewport.
     */
    constructor(mapTilesTextureName, gridData, width, height, tileViewWidth, tileViewHeight) {
        super(ProgramManager.getProgram('2DGrid'));
        this.className = 'Renderable2DGrid';

        this.dataArray = new Uint16Array(width * height);

        /*
        [0][0] = 0 = Map data tiles width.

        [0][1] = 1 = Map data tiles height.

        [0][2] = 2 = Map tiles in the texture width.

        [0][3] = 3 = Map tiles in the texture height.


        [1][0] = 4 = Map viewport width.

        [1][1] = 5 = Map viewport height.

        [1][2] = 6 = Map viewport x1.

        [1][3] = 7 = Map viewport y1.


        [2][0] = 8 = TileSize width normalized.

        [2][1] = 9 = TileSize height normalized. 
        */
        this.shaderTileData = [];
        this.mapTilesTexture = TextureManager.getTexture(mapTilesTextureName);
        this.addTexture(this.mapTilesTexture);
        this.mapTilesDataTextureName = mapTilesTextureName + 'Data' + dataTexCounter++;
        this.setGridData(gridData, width, height, tileViewWidth, tileViewHeight);
    }

    updateDataArray(data, x1, y1, width, height) {
        if (data == null)
            return;
        let dataY = 0;
        for (let y = y1; y < y1 + height; y++) {
            this.dataArray.set(data.slice(dataY * width, (dataY + 1) * width), x1 + (y * width));
            dataY++;
        }
    }

    /**
     * Updates the data texture by quadrant. Quadrant is specified by the x1, y1, 
     * width, and height parameters.
     * 
     * @param {Array} data Array of values indicating the index of each sprite.
     * @param {number} x1 Top left x position to update.
     * @param {number} y1 Top left y position to update.
     * @param {number} width Width (in tiles) of data to update.
     * @param {number} height Height (in tiles) of data to update.
     */
    updateGridData(data, x1, y1, width, height) {
        this.updateDataArray(data, x1, y1, width, height);
        this.buildShaderTileData(x1, y1, width, height);
        TextureManager.updateDataTexture(this.mapTilesDataTextureName, this.dataArray, x1, y1, width, height);
        this.requestRedraw();
    }

    /**
     * 
     * 
     * @param {*} data 
     * @param {*} x1 
     * @param {*} y1 
     * @param {*} width 
     * @param {*} height 
     */
    updateGridDataViewport(data, x1, y1, width, height, viewportWidth, viewportHeight) {
        this.updateDataArray(data, x1, y1, width, height);
        this.buildShaderTileData(x1, y1, viewportWidth, viewportHeight);
        TextureManager.updateDataTexture(this.mapTilesDataTextureName, this.dataArray, x1, y1, width, height);
        this.requestRedraw();
    }

    setGridData(data, width, height, tileViewWidth, tileViewHeight) {
        this.updateDataArray(data, 0, 0, width, height);
        let texture = TextureManager.addDataTexture(this.mapTilesDataTextureName, this.dataArray, RenderManager.GL.R16UI, RenderManager.GL.RED_INTEGER, RenderManager.GL.UNSIGNED_SHORT, -1, -1, width, height);
        this.addTexture(texture);
        /*
        [0][0] = 0 = Map data tiles width.
        [0][1] = 1 = Map data tiles height.
        [0][2] = 2 = Map tiles in the texture width.
        [0][3] = 3 = Map tiles in the texture height.

        [1][0] = 4 = Map viewport width.
        [1][1] = 5 = Map viewport height.
        [1][2] = 6 = Map viewport x1.
        [1][3] = 7 = Map viewport y1.

        [2][0] = 8 = TileSize width normalized.
        [2][1] = 9 = TileSize height normalized. 
        */
        this.shaderTileData = [
            width, height, this.mapTilesTexture.framesWidth, this.mapTilesTexture.framesHeight, 
            tileViewWidth, tileViewHeight, 0, 0, 
            1 / this.mapTilesTexture.framesWidth, 1 / this.mapTilesTexture.framesHeight, 1, 1, 
            1, 1, 1, 1
        ];
    }

    /**
     * Sets the viewport of the map grid to x1, y1, width, height (in tiles).
     * 
     * @param {number} viewportX1 Top left x position of the viewport.
     * @param {number} viewportY1 Top left y position of the viewport.
     * @param {number} viewportWidth Width of the viewport
     * @param {number} viewportHeight 
     */
    buildShaderTileData(viewportX1, viewportY1, viewportWidth, viewportHeight) {
        this.shaderTileData[4] = viewportWidth;
        this.shaderTileData[5] = viewportHeight;
        this.shaderTileData[6] = viewportX1;
        this.shaderTileData[7] = viewportY1;
    }

    onEnd() {
        this.removeFromViewports();
        TextureManager.removeTexture(this.mapTilesDataTextureName);
    }

    /**
     * Updates the uniforms of this renderable. Requires a position matrix for 
     * perspective calculations by the RendererManager.
     * 
     * @param {Matrix3} positionMatrix Position matrix of this renderable.
     */
    setUniformData(positionMatrix) {
        if (this.textures.length < 2)
            return false;
        this.program.setUniforms({
            'u_color': this.color.color,
            'u_matrix': positionMatrix,
            'u_depth': this.depth,
            'u_tileData': this.shaderTileData,
            'u_texture': 0,
            'u_mapDataTexture': 1,
        });
        return true;
    }
}

import Renderable from './Renderable';
import Matrix3 from '../render/WebGL/Matrix3';
import ProgramManager from '../manager/ProgramManager';

/**
 * Renderable2DMultitex is a renderable component, similar to the Renderable2D, except
 * it supports multiple textures. The program assigned to this renderable computes what to
 * do with the shaders passed.
 */
export default class Renderable2DMultitex extends Renderable {
    constructor(program = ProgramManager.getProgram('Default')) {
        super(program);
        this.className = 'Renderable2DMultitex';
    }

    /**
     * Adds a texture to this renderable.
     * 
     * @param {textureJSONObject} textureObject Texture reference object, from TextureManager.
     */
    addTexture(textureObject) {
        this.textures.push(textureObject);
    }

    /**
     * Removes the nth texture from this component. Textures are pushed to end of list when added.
     * 
     * @param {number} textureIndex Index of texture to remove.
     */
    removeTexture(textureIndex) {
        if (textureIndex < 0 || textureIndex >= this.textures.length)
            return;

        this.textures.splice(textureIndex, 1);
    }

    /**
     * Removes all textures from this renderable.
     */
    removeTextures() {
        this.textures = [];
    }
}

