/**
 * Two points which represent a bounding square.
 * 
 * TODO: Add z index for 3D.
 */
export default class Bounds {
    /**
     * 
     * @param {number} x The x position of the bounding box. (x1)
     * @param {number} y The y position of the bounding box. (y1)
     * @param {number} w The end x position, or width, of the bounding box. (x2)
     * @param {number} h The end x position, or height, of the bounding box. (y2)
     */
    constructor(x, y, w, h) {
        this.p1 = new Point(x, y);
        this.p2 = new Point(w, h);
    }

    /**
     * Sets the bounding box to a new position.
     * 
     * @param {number} x The x position of the bounding box. (x1)
     * @param {number} y The y position of the bounding box. (y1)
     * @param {number} w The end x position, or width, of the bounding box. (x2)
     * @param {number} h The end x position, or height, of the bounding box. (y2)
     */
    set(x, y, w, h) {
        this.p1.set(x, y);
        this.p2.set(w, h);
    }

    /**
     * Checks to see if the point [parameter 0] is within bounds.
     * 
     * @param {Point} point Point to check within bounds.
     */
    isInBounds(point) {
        return (point.x >= this.p1.x && point.x <= this.p2.x
                && point.y >= this.p1.y && point.y <= this.p2.y);
    }
}

/**
 * A color with r, g, b, a values between 0 and 1.
 * 
 * Contains instatiation functions for creating default 
 * colors without explicitly defining the values.
 */
export default class Color {
    /**
     * Builds a color into an array for easy access for WebGL.
     * 
     * @param {number} r Red value between 0 - 1
     * @param {number} g Green value between 0 - 1
     * @param {number} b Blue value between 0 - 1
     * @param {number} a Alpha value between 0 - 1
     */
    constructor(r = 1, g = 1, b = 1, a = 1) {
        this.color = [this.minmax(r), this.minmax(g), this.minmax(b), this.minmax(a)];
    }

    minmax(val, min = 0, max = 1) {
        return Math.max(min, Math.min(max, val));
    }

    /**
     * 
     * @param {number} r Red value between 0 - 1. Defaults to previous value.
     * @param {number} g Green value between 0 - 1. Defaults to previous value.
     * @param {number} b Blue value between 0 - 1. Defaults to previous value.
     * @param {number} a Alpha value between 0 - 1. Defaults to previous value.
     */
    set(r = this.color[0], g = this.color[1], b = this.color[2], a = this.color[3]) {
        this.color = [this.minmax(r), this.minmax(g), this.minmax(b), this.minmax(a)];
    }

    /**
     * Returns a new color with the color green. Value: #00FF00 with alpha 1.
     */
    static green() {
        return new Color(0, 1, 0, 1);
    }

    /**
     * Returns a new color with the color blue. Value: #0000FF with alpha 1.
     */
    static blue() {
        return new Color(0, 0, 1, 1);
    }

    /**
     * Returns a new color with the color red. Value: #FF0000 with alpha 1.
     */
    static red() {
        return new Color(1, 0, 0, 1);
    }

    /**
     * Returns a new color with the color pink. Value: #FF1493 with alpha 1.
     */
    static pink() {
        return new Color([1, 0.078, 0.576, 1]);
    }
}

/**
 * A point with x, y, z values. It may also be referred to as a Vector.
 */
export default class Point {
    /**
     * An x, y, z point. Default parameters are set as 0.
     * 
     * @param {number} x X position of the point.
     * @param {number} y Y position of the point.
     * @param {number} z Z position of the point.
     */
    constructor(x = 0, y = 0, z = 0) {
        /**
         * {number} X position of the point.
         */
        this.x = x;

        /**
         * {number} Y position of the point.
         */

        this.y = y;
        /**
         * {number} Z position of the point.
         */
        this.z = z;
    }

    /**
     * Sets the point to a new x, y, z. Previous values are set
     * as default parameters.
     * 
     * @param {number} x X position of the point.
     * @param {number} y Y position of the point.
     * @param {number} z Z position of the point.
     */
    set(x = this.x, y = this.y, z = this.z) {
        this.x = x;
        this.y = y;
        this.z = z;
    }

    /**
     * Returns a copy of a point.
     * 
     * @param {Point} point Point to copy.
     */
    static copy(point) {
        return new Point(point.x, point.y, point.z);
    }

    /**
     * Divides the point by a number. The first parameter will define all x, y, z values for division
     * if the 2nd and 3rd are not specified.
     * 
     * @param {number} divX 
     * @param {number} divY 
     * @param {number} divZ 
     * 
     * @returns {Point} This point after modification.
     */
    divide(divX, divY = divX, divZ = divX) {
        this.x /= divX;
        this.y /= divY;
        this.z /= divZ;
        return this;
    }

    /**
     * Multiplies the point by a number. The first parameter will define all x, y, z values for multiplication
     * if the 2nd and 3rd are not specified.
     * 
     * @param {number} divX 
     * @param {number} divY 
     * @param {number} divZ 
     * 
     * @returns {Point} This point after modification.
     */
    multiply(multiX, multiY = multiX, multiZ = multiX) {
        this.x *= multiX;
        this.y *= multiY;
        this.z *= multiZ;
        return this;
    }

    /**
     * Adds the point by a number(s). The first parameter will define all x, y, z values for addition
     * if the 2nd and 3rd are not specified.
     * 
     * @param {number} divX 
     * @param {number} divY 
     * @param {number} divZ 
     * 
     * @returns {Point} This point after modification.
     */
    add(addX, addY = addX, addZ = addX) {
        this.x += addX;
        this.y += addY;
        this.z += addZ;
        return this;
    }

    /**
     * Subtracts the point by a number(s). The first parameter will define all x, y, z values for subtraction
     * if the 2nd and 3rd are not specified.
     * 
     * @param {number} divX 
     * @param {number} divY 
     * @param {number} divZ 
     * 
     * @returns {Point} This point after modification.
     */
    subtract(subX, subY = subX, subZ = subX) {
        this.x -= subX;
        this.y -= subY;
        this.z -= subZ;
        return this;
    }

    /**
     * Gets the length of this point as a vector. sqrt(x^2 + y^2 + z^2).
     */
    length() {
        return Math.sqrt((this.x * this.x) + (this.y * this.y) + (this.z * this.z));
    }

    /**
     * Normalizes this vector to a unit vector.
     */
    normalize() {
        let len = this.length();
        if (len !== 0)
            this.divide(len);
    }
}

/**
 * Experemental! Still looking at other ways to implement this that would be more useful
 * for the engine, and lower lookup, post, and watch runtimes.
 * 
 * A "PostBoard" is a messaging system that anyone who has reference to may "post" updates to.
 * Posts are any type of data, assigned by key.
 * Other objects may look at "posts", and continuously watch a "post".
 */

export default class PostBoard {
    constructor() {
        this.posts = {};
        this.watchers = {};
        this.watcherID = 0;
    }

    look(key) {
        return this.posts[key];
    }

    post(key, message) {
        this.posts[key] = message;
        let watcherKeys = Object.keys(this.watchers[key]);
        for (let i = 0; i < this.watcherKeys.length; i++) {
            this.watchers[key][watcherKeys[i]](message);
        }
    }

    watch(key, callback, id = null) {
        if (id == null)
            id = this.watcherID++;
        this.watchers[key][id] = callback;
        return id;
    }

    close(key, id) {
        delete this.watchers[key][id];
    }

    closeAll(id) {
        let keyKeys = Object.keys(this.watchers);
        for (let i = 0; i < this.keyKeys.length; i++) {
            delete this.watchers[keyKeys[i]][id];
        }
    }
}

import Manager from './Manager';

export class _AudioManager extends Manager {
    constructor() {
        super();
        this.masterVolume = 1;
        this.audioCtx = null;

        this.sounds = {};
    }

    setMasterVolume(volume) {
        this.masterVolume = Math.max(0, Math.min(1, volume));
    }

    addSound(soundName, soundAssetPath) {
        return this._createSoundBufferFromAsset(soundAssetPath).then((soundInfo) => {
            this.sounds[soundName] = soundInfo;
            return this.sounds[soundName];
        });
    }

    _createSoundBufferFromAsset(assetPath) {
        let soundInfo = {
            buffer: null,
        };

        return new Promise((res, rej) => {
            let request = new XMLHttpRequest();
            request.open('GET', assetPath, true);
            request.responseType = 'arraybuffer';
            request.addEventListener('load', () => {
                this.audioCtx.decodeAudioData(request.response, (buffer) => {
                    soundInfo.buffer = buffer;
                    return res(soundInfo);
                }, (err) => {
                    console.error(err);
                });
            });
            request.send();
        });
    }

    playSound(soundName, volume = 1) {
        // Find the sound to play.
        let sound = this.sounds[soundName];
        if (sound == null)
            return false;

        if (!this.audioCtx.createGain) // For older browser support
            this.audioCtx.createGain = this.audioCtx.createGainNode;

        // Create the gain preference and set the volume
        let gainNode = this.audioCtx.createGain();
        gainNode.gain.value = volume * this.masterVolume;

        // Set the audio buffer source.
        let source = this.audioCtx.createBufferSource();
        source.buffer = sound.buffer;    

        // Connect gain node to buffer.
        source.connect(gainNode);      

        // Set default audio output.
        gainNode.connect(this.audioCtx.destination);      

        if (!source.start) // For older browser support
            source.start = source.noteOn;

        // Start the buffer playback.
        source.start(0);        
    }

    start() {
        try {
            // Fix up for prefixing
            window.AudioContext = window.AudioContext||window.webkitAudioContext;
            this.audioCtx = new AudioContext();
        }
        catch(e) {
            console.info('Web Audio API is not supported in this browser');
        }
    }

    removeSound(soundName) {
        delete this.sounds[soundName];
    }

    end() {
        let soundNames = {};
        //remove all loaded sounds
    }
}

