import Manager from './Manager';
import DOMManager from './DOMManager';
import * as VertexShader from '../const/VertexShader';
import * as FragmentShader from '../const/FragmentShader';


/**
 * Manages WebGL Programs so that they only need to be created once across the engine
 * allowing multiple objects to still share the same reference.
 * 
 * Elliminates the need for the programmer to compile shaders and create programs, and eases 
 * on-the-fly Shader compiling.
 */
export class _ProgramManager extends Manager {
    constructor() {
        super();
        this.programIDCounter = 0;
        this.programs = {};
    }

    /**
     * Creates default programs for easy reference later on.
     */
    start() {
        if (DOMManager.GL == null)
            return;
        this.addProgram('Default', VertexShader.DEFAULT_V, FragmentShader.DEFAULT_F, {
            'u_color': 'uniform4fv',
            'u_matrix': 'uniformMatrix3fv',
            'u_depth': 'uniform1f',
            'u_texture': 'uniform1i',
            'u_subTexcoord': 'uniform4fv',
        });
        this.addProgram('2DGrid', VertexShader.DEFAULT_V, FragmentShader.TILEMAP_F, {
            'u_color': 'uniform4fv',
            'u_matrix': 'uniformMatrix3fv',
            'u_depth': 'uniform1f',
            'u_mapDataTexture': 'uniform1i',
            'u_texture': 'uniform1i',
            'u_tileData': 'uniformMatrix4fv'
        });
    }

    /**
     * Returns a Program JSON Object containing the program, name and id.
     * 
     * @param {string} programKey Name of the program.
     * 
     * @returns {Program Object} Returns a JSON object with Program data.
     */
    getProgram(programKey) {
        if (this.programs[programKey] == null)
            throw 'Program does not exist!';
        return this.programs[programKey];
    }

    /**
     * Creates a Program JSON Object and initializes the program and metadata.
     * The program is added to the programs array.
     * 
     * @param {string} programName Name of the program.
     * @param {string} vertexShaderSource Source code of the vertex shader.
     * @param {string} fragmentShaderSource Source code of the fragment shader.
     * @param {Object} uniforms An object of 'string': 'number' values indicating the uniform variable name & data type enum value.
     */
    addProgram(programName, vertexShaderSource, fragmentShaderSource, uniforms) {
        let program = this._createProgram(vertexShaderSource, fragmentShaderSource);
        let uniformSetters = this._createProgramLocationSetters(program, uniforms);
        this.programs[programName] = {
            name: programName,
            id: this.programIDCounter++,
            program,
            uniformSetters,
            setUniforms: (uniformDataMapping) => {
                let setters = Object.keys(uniformDataMapping);
                for (let i = 0; i < setters.length; i++) {
                    uniformSetters[setters[i]](DOMManager.GL, uniformDataMapping[setters[i]]);
                }
            }
        };
    }

    /**
     * Creates a WebGL program from a compiled vertex and fragment shader. The program is returned.
     * 
     * @param {CompiledVertexShader} vertexShader A compiled vertex shader.
     * @param {CompiledFragmentShader} fragmentShader A compiled fragment shader.
     * 
     * @returns {Program} A WebGL program. Null if unsuccessful.
     */
    _createShadersProgram(vertexShader, fragmentShader) {
        let program = DOMManager.GL.createProgram();
        DOMManager.GL.attachShader(program, vertexShader);
        DOMManager.GL.attachShader(program, fragmentShader);
        DOMManager.GL.linkProgram(program);
        let success = DOMManager.GL.getProgramParameter(program, DOMManager.GL.LINK_STATUS);
        if (success) {
            return program;
        }

        console.error(DOMManager.GL.getProgramInfoLog(program));
        DOMManager.GL.deleteProgram(program);
        return null;
    }

    /**
     * Creates a shader of either Vertex or Fragment type and returns the compiled version.
     * 
     * @param {GLShaderType} type A GL shader type of either VERTEX_SHADER or FRAGMENT_SHADER.
     * @param {string} source Source code for the shader type.
     * 
     * @returns {CompiledShader} A compiled shader. Null if unsuccessful.
     */
    _createShader(type, source) {
        let shader = DOMManager.GL.createShader(type);
        DOMManager.GL.shaderSource(shader, source);
        DOMManager.GL.compileShader(shader);
        let success = DOMManager.GL.getShaderParameter(shader, DOMManager.GL.COMPILE_STATUS);
        if (success) {
            return shader;
        }

        console.error(DOMManager.GL.getShaderInfoLog(shader));
        DOMManager.GL.deleteShader(shader);
        return null;
    }

    /**
     * Compiles the vertex shader and fragment shader and returns a WebGL program.
     * 
     * @param {string} vertexShaderSource Source code for a vertex shader.
     * @param {string} fragmentShaderSource Source code for a fragment shader.
     * 
     * @returns {Program} A program compiled from the two shader sources.
     */
    _createProgram(vertexShaderSource, fragmentShaderSource) {
        let vertexShader = this._createShader(DOMManager.GL.VERTEX_SHADER, vertexShaderSource);
        let fragmentShader = this._createShader(DOMManager.GL.FRAGMENT_SHADER, fragmentShaderSource);

        return this._createShadersProgram(vertexShader, fragmentShader);
    }

    _createProgramLocationSetters(program, uniforms) {
        let uniformLocationSetters = {};
        let uniformKeys = Object.keys(uniforms);
        for (let i = 0; i < uniformKeys.length; i++) {
            let location = DOMManager.GL.getUniformLocation(program, uniformKeys[i]);
            uniformLocationSetters[uniformKeys[i]] = this._getUniformSetterFromString(location, uniforms[uniformKeys[i]]);
        }
        return uniformLocationSetters;
    }

    _getUniformSetterFromString(location, type) {
        return {
            'uniform4fv': (gl, uniformData) => { gl.uniform4fv(location, uniformData); },
            'uniformMatrix3fv': (gl, uniformData) => { gl.uniformMatrix3fv(location, false, uniformData); },
            'uniformMatrix4fv': (gl, uniformData) => { gl.uniformMatrix4fv(location, false, uniformData); },
            'uniform1f': (gl, uniformData) => { gl.uniform1f(location, uniformData); },
            'uniform1i': (gl, uniformData) => { gl.uniform1i(location, uniformData); },
        }[type];
    }
}

/**
 * Singleton reference to the WebGL Program Manager.
 */
const ProgramManager = new _ProgramManager();
export default ProgramManager;

import Manager from './Manager';
import DOMManager from './DOMManager';
import ProgramManager from './ProgramManager';
import SceneManager from './SceneManager';
import * as VertexShader from '../const/VertexShader';
import * as FragmentShader from '../const/FragmentShader';
import Matrix3 from '../render/WebGL/Matrix3';
import { TextureManager } from '../entry';

/**
 * Manages the WebGL2 rendering of all renderable components in the scene.
 * This manager is not intended to be referenced directly.
 */
export class _RenderManager extends Manager {
    constructor() {
        super();
        this.GL = null;
        this.currentProgram = null;
        this.activeTextureIDs = [];

        //text
        this.RenderableTextIDCounter = 0;

        //last update microsecond
        this.lastUpdate = -1;
    }

    /**
     * Initial setup on GL rendering. 
     */
    start() {
        this.GL = DOMManager.GL;
        if (this.GL == null)
            return;

        this._updateProgram(ProgramManager.getProgram('Default'));

        this.positionAttributeLocation = this.GL.getAttribLocation(this.currentProgram.program, "a_position");

        //textures
        this.texcoordAttributeLocation = this.GL.getAttribLocation(this.currentProgram.program, "a_texcoord");

        let positionBuffer = this.GL.createBuffer();

        this.GL.bindBuffer(this.GL.ARRAY_BUFFER, positionBuffer);

        this.GL.bufferData(this.GL.ARRAY_BUFFER, new Float32Array([0, 0, 1, 0, 0, 1, 0, 1, 1, 0, 1, 1]), this.GL.STATIC_DRAW);

        this.vao = this.GL.createVertexArray();

        this.GL.bindVertexArray(this.vao);

        this.GL.enableVertexAttribArray(this.positionAttributeLocation);

        let size = 2;
        let type = this.GL.FLOAT;
        let normalize = false;
        let stride = 0;
        let vertexOffset = 0;
        this.GL.vertexAttribPointer(this.positionAttributeLocation, size, type, normalize, stride, vertexOffset);

        //textures
        this.texcoordBuffer = this.GL.createBuffer();
        this.GL.bindBuffer(this.GL.ARRAY_BUFFER, this.texcoordBuffer);
        this.GL.bufferData(this.GL.ARRAY_BUFFER, new Float32Array([0, 0, 1, 0, 0, 1, 0, 1, 1, 0, 1, 1]), this.GL.STATIC_DRAW);

        // Turn on the attribute
        this.GL.enableVertexAttribArray(this.texcoordAttributeLocation);

        // Tell the attribute how to get data out of colorBuffer (ARRAY_BUFFER)
        let t_size = 2;          // 3 components per iteration
        let t_type = this.GL.FLOAT;   // the data is 32bit floats
        let t_normalize = true;  // convert from 0-255 to 0.0-1.0
        let t_stride = 0;        // 0 = move forward size * sizeof(type) each iteration to get the next color
        let t_offset = 0;        // start at the beginning of the buffer
        this.GL.vertexAttribPointer(
            this.texcoordAttributeLocation, t_size, t_type, t_normalize, t_stride, t_offset);


        this.GL.enable(this.GL.DEPTH_TEST);
        this.GL.depthFunc(this.GL.LESS);      
        this.GL.enable(this.GL.BLEND);
        //this.GL.blendFuncSeparate(this.GL.GL_SRC_ALPHA, this.GL.GL_ONE_MINUS_SRC_ALPHA, this.GL.GL_ONE, this.GL.GL_ONE_MINUS_SRC_ALPHA);
    }

    /**
     * Checks if the program for the next object to draw must be changed.
     * If it does, it will change WebGL programs.
     * 
     * @param {ProgramObject} program A program object returned from ProgramManager.getProgram()
     */
    _updateProgram(program) {
        if (this.currentProgram == null || this.currentProgram.id != program.id) {
            this.GL.useProgram(program.program);
            this.currentProgram = program;
        }
    }

    _updateTextures(textures) {
        let newActiveTextures = [];
        for (let t = 0; t < textures.length; t++) {
            newActiveTextures.push(textures[t].id);
            this.GL.activeTexture(this.GL.TEXTURE0 + t);
            this.GL.bindTexture(this.GL.TEXTURE_2D, textures[t].tex);
        }
    }

    registerTextRenderable(textRenderable, width, height) {
        textRenderable.renderableTextID = this.RenderableTextIDCounter++ % 1024;
        if (TextureManager.getTexture('TextData') == null) {
            //data of the text data to be read in the FS for rendering. Defaults to 0's. If you have a character at location 0, 1st column, it will be the "empty" character.
            let textDataTextureData = new Uint16Array(width * height);
            TextureManager.addDataTexture('TextData', textDataTextureData, RenderManager.GL.R16UI, RenderManager.GL.RED_INTEGER, RenderManager.GL.UNSIGNED_SHORT, -1, -1, width, height);
        }
    }

    forceUpdate() {
        if (performance.now() + 0.1 <= this.lastUpdate)
            return;
        requestAnimationFrame((time) => {
            if (time > this.lastUpdate) {
                this.lastUpdate = time;
                RenderManager.update();
            }
        });
    }

    /**
     * Update function for updating all renderable objects in each viewport in the current scene.
     */
    update() {
        if (this.GL == null)
            return;
        // this.GL.clearColor(0, 0, 0, 0);
        // this.GL.clearDepth(1.0);
        this.GL.clear(this.GL.COLOR_BUFFER_BIT | this.GL.DEPTH_BUFFER_BIT);

        let scene = SceneManager.getCurrentScene();

        if (scene == null)
            return;

        let viewports = scene.viewports;

        for (let vi = 0; vi < viewports.length; vi++) {
            let viewport = viewports[vi];

            let viewPortWidth = viewport._rendererBounds.p2.x;
            let viewPortHeight = viewport._rendererBounds.p2.y;

            this.GL.viewport(viewport._rendererBounds.p1.x, viewport._rendererBounds.p1.y, viewPortWidth, viewPortHeight);

            //setup camera from viewport

            let renderables = viewport.renderables;
            let renderableKeys = Object.keys(renderables);
            for (let ri = 0; ri < renderableKeys.length; ri++) {
                let renderable = renderables[renderableKeys[ri]];

                if (renderable.hasPaused)
                    continue;

                this._updateProgram(renderable.program);

                if (!renderable.setUniformData(Matrix3.projection(viewPortWidth, viewPortHeight).multiply(renderable.getMatrix()).m))
                    continue;

                //if (renderable.updateTextures) {
                    this._updateTextures(renderable.textures);
                    renderable.updateTextures = false;
                //}

                this.GL.drawArrays(renderable.primitiveType, 0, renderable.primitiveCount);
            }
        }
    }
}

/**
 * Singleton reference to the Rendering Manager.
 */
const RenderManager = new _RenderManager();
export default RenderManager;

import Manager from './Manager';
import GameObject from '../object/GameObject';

/**
 * A manager which manages all scenes and passes commands to the current scene.
 * 
 * Manages scenes on a stack, pausing scenes below them (stopping rendering), and managing
 * the top scene for triggering updates on all objects on the scene.
 * 
 * GameObjects, components, or anything else can use the SceneManager to reference the
 * current active scene. There are also wrapper functions for passing SceneObjects and
 * Viewports to the current scene for registration with that scene.
 */
export class _SceneManager extends Manager {
    constructor() {
        super();
        this.scenes = [];
    }

    /**
     * Adds a SceneObject to the current scene on the stack. The scene will hold 
     * reference and manage the SceneObject.
     * 
     * @param {SceneObject} sceneObject The SceneObject that is being registered.
     */
    registerSceneObject(sceneObject) {
        let scene = this.getCurrentScene();
        if (scene != null)
            return scene.registerSceneObject(sceneObject);
        else
            return () => {};
    }

    /**
     * Addsa viewport to the current scene on the stack. The scene will hold reference
     * to the viewport.
     * 
     * @param {viewport} viewport The Viewport that is being registered.
     */
    registerViewport(viewport) {
        let scene = this.getCurrentScene();
        if (scene != null)
            return scene.registerViewport(viewport);
    }

    /**
     * Gets the current scene on the stack. This scene will be the one currently
     * used in the update loop.
     */
    getCurrentScene() {
        if (this.scenes.length > 0)
            return this.scenes[this.scenes.length - 1];
        return null;
    }

    /**
     * Starts the current scene on the stack.
     */
    start() {
        if (this.scenes.length > 0) {
            this.scenes[this.scenes.length - 1].start();
        }
    }

    /**
     * Updates the current scene and all SceneObjects that are assigned to that
     * scene.
     */
    update() {
        let scene = this.getCurrentScene();
        if (scene != null) {
            scene.update();
            for (let i = 0; i < scene.sceneObjects.length; i++) {
                scene.sceneObjects[i].update();
            }
        }
    }

    /**
     * Ends all scenes, starting from the top & poping each scene off the stack as it goes.
     */
    end() {
        while (this.scenes.length > 0) {
            this.scenes[this.scenes.length - 1].pop().end();
        }
    }

    /**
     * Pauses the current scene.
     */
    pause() {
        if (this.scenes.length > 0)
            this.scenes[this.scenes.length - 1].pause();
    }

    /**
     * Unpauses the current scene.
     */
    unpause() {
        if (this.scenes.length > 0)
            this.scenes[this.scenes.length - 1].unpause();
    }

    /**
     * Adds the new scene to the top of the stack, pausing the current
     * scene first.
     * 
     * @param {Scene} scene The new scene.
     */
    addScene(scene) {
        if (this.scenes.length > 0) {
            this.scenes[this.scenes.length - 1].pause();
        }
        this.scenes.push(scene);
    }

    /**
     * Removes the top scene from the stack, calls its end() function,
     * and unpauses the scene below it.
     */
    removeScene() {
        this.scenes.pop().end();
        this.scenes[this.scenes.length - 1].unpause();
    }
}

/**
 * Singleton reference to the Scene Manager.
 */
const SceneManager = new _SceneManager();
export default SceneManager;

import Manager from './Manager';
import DOMManager from './DOMManager';
import AudioManager from './AudioManager';


/**
 * Manages textures in the engine so the RendererManager and Renderables
 * can reference one location for Textures. Essentially a texture library.
 */
export class _TextureManager extends Manager {
    constructor() {
        super();
        this.textures = {};
        this.textureIDs = [];
    }

    /**
     * Returns a texture JSON Object containing the texture, texture data, name and id.
     * 
     * @param {number} texID Name of the texture.
     * 
     * @returns {Texture Object} Returns a JSON object with Texture data.
     */
    getTexture(texID) {
        if (this.textures[texID] == null)
            return null;
        return this.textures[texID];
    }

    _setAvailableTextureID() {
        for (let i = 0; i < this.textureIDs.length; i++)
            if (!this.textureIDs[i]) {
                this.textureIDs[i] = true;
                return i;
            }
        this.textureIDs.push(true);
        return this.textureIDs.length - 1;
    }

    /**
     * Creates a Texture JSON Object and initializes the Texture with WebGL.
     * The Texture is added to the textures array.
     * 
     * @param {string} texName Name of the texture.
     * @param {string} textureImageAsset Path to the texture image to load.
     * @param {number} frameWidth Width of sprite in spritesheet. -1 for full.
     * @param {number} frameHeight Height of sprite in spritesheet. -1 for full.
     * @param {JSON Object} glyphInfo Object defining additional info for letters to be used as a mapping for writing text.
     * Fonts should have these for displaying letters. Format the ojject like so:
     * {
     *     //height of all letters
     *     height: 8, 
     * 
     *     //letter corrosponding to the location and size defined inside.
     *     'A': {
     *         //the x position in pixels for this character.
     *         x: 0,
     * 
     *         //width in pixels for this character.
     *         width: 8
     *     }
     * }
     * 
     * @returns {Promise} A pending promise which will return the texture reference after complete.
     */
    addTexture(texName, textureImageAsset, frameWidth, frameHeight, glyphInfo = null) {
        return this._createTextureFromAsset(textureImageAsset).then((textureData) => {
            let texObj = this.textures[texName] = Object.assign({
                name: texName,
                id: this._setAvailableTextureID(),
                frameWidth,
                frameHeight,
                glyphInfo,
            }, textureData);
            this.textures[texName].framesWidth = texObj.width / texObj.frameWidth;
            this.textures[texName].framesHeight = texObj.height / texObj.frameHeight;
            return this.textures[texName];
        })
    }

    /**
     * 
     * @param {string} texName The name of the texture.
     * @param {Array *} textureData An array object of data, array type depending on the textureInternalFormat.
     * @param {GLint} textureInternalFormat Internal texture format type.
     * @param {GLint} textureFormat Texture format type.
     * @param {number} frameWidth Width of each sub sprite frame.
     * @param {number} frameHeight Height of each sub sprite frame.
     * @param {number} width Width of the texture.
     * @param {number} height Height of the texture.
     */
    addDataTexture(texName, textureData, textureInternalFormat, textureFormat, textureByteType, frameWidth, frameHeight, width, height) {
        let tex = this._createTextureFromData(textureData, textureInternalFormat, textureFormat, textureByteType, width, height);
        this.textures[texName] = Object.assign({
            name: texName,
            id: this._setAvailableTextureID(),
            frameWidth,
            frameHeight,
            width,
            height,
            framesWidth: width / frameWidth,
            framesHeight: height / frameHeight,
            textureInternalFormat,
            textureFormat,
            textureByteType,
        }, {tex});
        return this.textures[texName];
    }

    removeTexture(texName) {
        if (this.textures[texName] == null)
            return;

        let id = this.textures[texName].id;
        this.textureIDs[id] = false;
        this.textures[texName] = null;
    }

    updateDataTexture(texName, textureData, x1, y1, width, height) {
        let texture = this.getTexture(texName);
        DOMManager.GL.bindTexture(DOMManager.GL.TEXTURE_2D, texture.tex);
        DOMManager.GL.texSubImage2D(DOMManager.GL.TEXTURE_2D, 0, x1, y1, width, height, texture.textureFormat, texture.textureByteType, textureData);
        return texture;
    }

    addGlyphInfoToTexture(texName, glyphInfo) {
        let texture = this.getTexture(texName);
        if (texture == null) {
            console.error('Texture does not exist!');
            return false;
        }
        texture.glyphInfo = glyphInfo;
    }

    _createTextureFromData(texData, textureInternalFormat, textureFormat, textureByteType, width, height) {
        let tex = DOMManager.GL.createTexture();

        DOMManager.GL.bindTexture(DOMManager.GL.TEXTURE_2D, tex);
        DOMManager.GL.pixelStorei(DOMManager.GL.UNPACK_FLIP_Y_WEBGL, false);

        DOMManager.GL.texParameteri(DOMManager.GL.TEXTURE_2D, DOMManager.GL.TEXTURE_MIN_FILTER, DOMManager.GL.NEAREST);
        DOMManager.GL.texParameteri(DOMManager.GL.TEXTURE_2D, DOMManager.GL.TEXTURE_MAG_FILTER, DOMManager.GL.NEAREST);
        DOMManager.GL.texParameteri(DOMManager.GL.TEXTURE_2D, DOMManager.GL.TEXTURE_WRAP_S, DOMManager.GL.CLAMP_TO_EDGE);
        DOMManager.GL.texParameteri(DOMManager.GL.TEXTURE_2D, DOMManager.GL.TEXTURE_WRAP_T, DOMManager.GL.CLAMP_TO_EDGE);

        DOMManager.GL.texImage2D(DOMManager.GL.TEXTURE_2D, 0, textureInternalFormat, width, height, 0, textureFormat, textureByteType, texData);

        return tex;
    }

    /**
     * Private function used for initializing a Texture from a path and
     * binding it with WebGL.
     * 
     * @param {string} asset Path to texture file.
     * 
     * @returns {Promise} Returns a pending promise.
     */
    _createTextureFromAsset(asset) {
        let texInfo = {
            tex: DOMManager.GL.createTexture(),
            width: 0,
            height: 0,
            framesWidth: 0,
            framesHeight: 0,
        };

        return new Promise((res, rej) => {
            let assetLoaded = new Image();
            assetLoaded.addEventListener('load', () => {
                texInfo.width = assetLoaded.width;
                texInfo.height = assetLoaded.height;

                DOMManager.GL.bindTexture(DOMManager.GL.TEXTURE_2D, texInfo.tex);

                DOMManager.GL.texParameteri(DOMManager.GL.TEXTURE_2D, DOMManager.GL.TEXTURE_WRAP_S, DOMManager.GL.CLAMP_TO_EDGE);
                DOMManager.GL.texParameteri(DOMManager.GL.TEXTURE_2D, DOMManager.GL.TEXTURE_WRAP_T, DOMManager.GL.CLAMP_TO_EDGE);

                DOMManager.GL.bindTexture(DOMManager.GL.TEXTURE_2D, texInfo.tex);
                DOMManager.GL.texImage2D(DOMManager.GL.TEXTURE_2D, 0, DOMManager.GL.RGBA, DOMManager.GL.RGBA, DOMManager.GL.UNSIGNED_BYTE, assetLoaded);
                DOMManager.GL.generateMipmap(DOMManager.GL.TEXTURE_2D);
                return res(texInfo);
            });
            assetLoaded.src = asset;
        })
    }
}

/**
 * Singleton reference to the WebGL Program Manager.
 */
const TextureManager = new _TextureManager();
export default TextureManager;

