export default class UIText extends UIElement {
    constructor(viewport, x, y, w, h, text) {
        super(new Point(x, y, 0), new Point(w, h, 0), 0);

        this.text = text;
        this.currentTextScale = 1;

        this.textObject = new SceneObject(new Point(x, y, 0), new Point(w, h, 0), 0);
        this.textObject.renderableText = new RenderableText(this.uiStyle.fontTexture);
        this.textObject.addComponent(this.textObject.renderableText);
        this.textObject.renderableText.addToViewport(viewport);
        this.setText(text, h / this.uiStyle.fontTexture.frameHeight);
        this.textObject.renderableText.setDepth(-2000);
    }

    translate(x, y) {
        this.transform.translate(x, y);
        this.textObject.transform.translate(x, y);
    }

    setPosition(x, y) {
        this.transform.setPosition(x, y, 0);
        this.textObject.transform.setPosition(x, y, 0);
    }

    setColor(r, g, b, a) {
        this.textObject.renderableText.setColor(r, g, b, a);
    }

    getTextWidth() {
        return this.textObject.renderableText.textData.textPixelWidth;
    }

    getText() {
        return this.text;
    }

    setText(text, scale = this.currentTextScale) {
        this.text = text;
        this.currentTextScale = Math.max(1, scale);
        this.textObject.renderableText.setText(text, this.currentTextScale);
    }

    onPause() {
        this.textObject.pause();
        this.textObject.renderableText.pause();
    }

    onUnpause() {
        this.textObject.unpause();
        this.textObject.renderableText.unpause();
        this.setText(this.text);
    }

    setDepth(depth) {
        this.textObject.renderableText.setDepth(depth);
    }

    end() {
        this.textObject.removeAllComponents();
        this.textObject.end();
    }
}

import { ClickController, Point, SceneObject, Renderable2D, UIManager, UIElement, InputManager, KeyCode } from '../../entry';
import UIPanel from './UIPanel';
import UIText from './UIText';

export default class UITextInput extends UIElement {
      constructor(viewport, x, y, w, h, text = '', requiresFocus = true) {
        super(new Point(x, y, 0), new Point(w, h, 0), 0);

        this.text = text;
        this.baseText = ''; //preceeding text always there.
        this.textView = text;
        this.textViewPassword = false;

        this.x = x;
        this.y = y;
        this.w = w;
        this.h = h;

        this.maxTextLength = 80;

        this.requiresFocus = requiresFocus;

        this.isFocussed = !this.requiresFocus;

        //called when enter/return key is pressed.
        this.onReturn = () => {};

        //Text
        this.textObject = new UIText(viewport, x + 5, y + 5, w, h, this.text);

        //text box
        this.textBoxObject = new UIPanel(viewport, x, y, w, h, this.uiStyle.textInputBoxTexture);

        //onclick
        this.textBoxObject.clickController = new ClickController(viewport, () => {
            if (!this.hasPaused)
                this.focus();
        }, () => {}, () => {
            if (!this.hasPaused)
                this.focus();
        }, () => {}, () => {
            if (!this.hasPaused)
                this.focus(false);
        });
        this.textBoxObject.addComponent(this.textBoxObject.clickController);

        //on keyboard input
        this.keyboardListenerHandle = null;
        this.setupKeyboardListener();
        this.setText(this.text);
    }

    focus(focus = true) {
        this.isFocussed = focus;
        this.setText(this.text);
    }

    isPassword(textViewPassword) {
        this.textViewPassword = textViewPassword;
        if (textViewPassword) {
            this.textView = this.text.replace(/./g, 'x');
        } else {
            this.textView = this.text;
        }
    }

    applyBaseText() {
        this.textView = this.baseText + this.textView;
    }

    getText() {
        return this.text;
    }

    _applyFilters() {
        this.isPassword(this.textViewPassword);
        this.applyBaseText();
    }

    _updateText(text) { 
        this.textObject.setText(text, this.h / this.uiStyle.fontTexture.frameHeight);
    }

    setText(text) {
        this.text = text;
        this._applyFilters();
        this._updateText(this.textView + (this.isFocussed || !this.requiresFocus ? '*' : ''));
    }

    setBaseText(text) {
        this.baseText = text;
        this._applyFilters();
        this._updateText(this.textView);
    }

    setOnReturn(callback) {
        this.onReturn = callback;
    }

    setDepth(depth) {
        this.textObject.setDepth(depth);
        this.textBoxObject.setDepth(depth);
    }

    setupKeyboardListener() {
        if (this.keyboardListenerHandle != null)
            this.keyboardListenerHandle.stop();
        this.keyboardListenerHandle = InputManager.events.watch(InputManager.EVENT.KEY, (event) => {
            if (!this.isFocussed && this.requiresFocus)
                return;

            //backspace
            if (event.code === 8)
                this.text = this.text.substring(0, this.text.length - 1);

            //return/enter
            if (event.code === 13 && InputManager.isKeyDown(KeyCode.ENTER)) {
                return this.onReturn();
            }

            if (this.text.length >= this.maxTextLength)
                return;

            //space & underscore are the same due to lack of characters (for now)
            if (event.code === 32 || event.code === 167)
                this.text += ' ';

            //numbers
            if ((event.code >= 48 && event.code <= 57))
                this.text += event.key;

            //letters
            if (event.code >= 64 && event.code <= 90)
                this.text += event.key;

            //      ; = , - . /
            if (event.code >= 186 && event.code <= 191 || event.code == 219 || event.code == 221)
                this.text += event.key;

            //special
            // if (event.code >= 48 && event.code <= 57)
            //     this.text += event.key;



            this.setText(this.text);
        }, false);
    }

    onPause() {
        this.textObject.pause();
        this.textBoxObject.clickController.pause();
        this.textBoxObject.pause();
        if (this.keyboardListenerHandle != null)
            this.keyboardListenerHandle.stop();
    }

    onUnpause() {
        this.textObject.unpause();
        this.textBoxObject.unpause();
        this.textBoxObject.clickController.unpause();
        this.setupKeyboardListener();
        this.setText(this.text);
    }

    onEnd() {
        this.textObject.end();
        this.textBoxObject.clickController.end();
        this.textBoxObject.end();
        if (this.keyboardListenerHandle != null)
            this.keyboardListenerHandle.stop();
    }
}

import SceneManager from '../manager/SceneManager';
import DOMManager from '../manager/DOMManager';
import Updateable from '../base/Updateable';
import Bounds from '../internal/Bounds';

/**
 * A viewport is a display port on the game canvas. Each scene can have multiple
 * viewports and renderable objects are assigned to the ones they want to be drawn on.
 * 
 * Viewports contain lighting and cameras.
 * 
 * GameObjects and their components exist across all viewports, it is up to the programmer
 * to specify the viewports to render to.
 * 
 * Examples on when to use viewports: 
 * - UI
 * - Minimaps
 * - Different views
 * 
 * TODO: An object of keys represented by 'z' indexes to store all 
 * renderable objects in render order.
 */
export default class Viewport extends Updateable {
    constructor(x, y, w, h) {
        super();
        this._bounds = null;
        this._rendererBounds = null;
        this.setBounds(x, y, w, h);

        this.renderables = {};
    }

    setBounds(x, y, w, h) {
        this._bounds = new Bounds(x, y, x + w, y + h);
        this._rendererBounds = new Bounds(x, DOMManager.canvasHeight - y - h, w, h);
    }

    getBounds() {
        return this._bounds;
    }

    /**
     * Register an Updateable component with this viewport for drawing.
     * 
     * @param {Renderable} renderable Renderable component to register.
     */
    registerRenderableComponent(renderable) {
        this.renderables[renderable.id] = renderable;
        let deregisterCallback = () => {
            delete this.renderables[renderable.id];
        }
        return deregisterCallback;
    }
}

/**
 * Baseclass for a Matrix. Use Matrix3 or Matrix4 for 2D and 3D matrix math.
 */
export default class Matrix {

    setPosition(x, y) {}

    setScale(scaleX, scaleY) {}

    setRotation(rotationDegree) {}

    copy() {}

    add(m) {}

    multiply(m) {}
}

import * as MathUtil from '../../utils/MathUtil';
import Matrix from './Matrix';


export default class Matrix3 extends Matrix {
    constructor(data = [1,0,0,0,1,0,0,0,1]) {
        super();
        //default x = 0, y = 0, scaleXY = 1, rotation = 0
        this.m = data;
        return this;
    }

    /**
     * Sets the translation positions of the matrix with x, y points. This overwrites the values and 
     * does not adjust the parameters to work relative to the rest of the matrix values.
     * 
     * @param {number} x X position.
     * @param {number} y Y position.
     */
    setPosition(x, y) {
        this.m[6] = x;
        this.m[7] = y;
    }

    /**
     * Sets the scale values of the matrix with w, h values. This overwrites the values and 
     * does not adjust the parameters to work relative to the rest of the matrix values.
     * 
     * @param {number} x X scale.
     * @param {number} y Y scale.
     */
    setScale(x, y) {
        this.m[0] = x;
        this.m[4] = y;
    }

    /**
     * Sets the matrix rotation values to rotate an object around the z axis. This overwrites the values and 
     * does not adjust the parameters to work relative to the rest of the matrix values.
     * 
     * @param {number} rotationDegree 
     */
    setRotation(rotationDegree) {
        let rad = MathUtil.degToRad(rotationDegree);
        let c = Math.cos(rad);
        let s = Math.sin(rad);
        this.m[0] = c;
        this.m[1] = -s;
        this.m[3] = s;
        this.m[4] = c;
    }

    /**
     * Makes a copy of the matrix3. This is a static function and can be called
     * from Matrix3 prototype.
     * 
     * @param {Matrix3} matrix The matrix3 to copy.
     */
    static copy(matrix) {
        return new Matrix3(matrix.m.slice(0, matrix.m.length));
    }

    /**
     * Adds a matrix to this matrix.
     * 
     * @param {Matrix3} m3 The matrix to add to this matrix.
     */
    add(m3) {
        throw 'unimplemented';
    }

    /**
     * Static function which returns a projection matrix converting the
     * top left coordinates to 0, 0, and bottom right to w, h.
     * 
     * (Converted from -1, -1 top left to 1, 1 bottom right.)
     * 
     * @param {number} w Width of the projection.
     * @param {number} h Height of the projection.
     */
    static projection(w, h) {
        return new Matrix3([
        2 / w, 0, 0,
        0, -2 / h, 0,
        -1, 1, 1]);
    }

    /**
     * Multiplies a matrix into this matrix.
     * 
     * @param {Matrix3} m3 The matrix to multiply to this matrix.
     */
    multiply(m3) {
        let mat = new Matrix3();
        mat.m[0] = (this.m[0] * m3.m[0]) + (this.m[3] * m3.m[1]) + (this.m[6] * m3.m[2]);
        mat.m[1] = (this.m[1] * m3.m[0]) + (this.m[4] * m3.m[1]) + (this.m[7] * m3.m[2]);
        mat.m[2] = (this.m[2] * m3.m[0]) + (this.m[5] * m3.m[1]) + (this.m[8] * m3.m[2]);

        mat.m[3] = (this.m[0] * m3.m[3]) + (this.m[3] * m3.m[4]) + (this.m[6] * m3.m[5]);
        mat.m[4] = (this.m[1] * m3.m[3]) + (this.m[4] * m3.m[4]) + (this.m[7] * m3.m[5]);
        mat.m[5] = (this.m[2] * m3.m[3]) + (this.m[5] * m3.m[4]) + (this.m[8] * m3.m[5]);

        mat.m[6] = (this.m[0] * m3.m[6]) + (this.m[3] * m3.m[7]) + (this.m[6] * m3.m[8]);
        mat.m[7] = (this.m[1] * m3.m[6]) + (this.m[4] * m3.m[7]) + (this.m[7] * m3.m[8]);
        mat.m[8] = (this.m[2] * m3.m[6]) + (this.m[5] * m3.m[7]) + (this.m[8] * m3.m[8]);
        return mat;
    }
}

import Scene from './Scene';
import { Viewport } from '../entry';
import DOMManager from '../manager/DOMManager';

/**
 * A basic representation of a working scene. Scenes need a viewport to give Renderable
 * components a place to be viewed by the RenderManager.
 * 
 * This scene only contains one viewport which takes up the size of the canvas element.
 */
export default class BasicScene extends Scene {
    constructor() {
        super();
        this.registerViewport(new Viewport(0, 0, DOMManager.canvas.width, DOMManager.canvas.height));
    }
}

import Updateable from '../base/Updateable';
import { SceneObject } from '../entry';

/**
 * Baseclass for all scenes. When creating a new scene, it should inherit this
 * class. 
 * Scenes keep track of their scene objects and viewports. The scene baseclass has
 * functions for registering these with the scene.
 */
export default class Scene extends Updateable {

    /**
     * Builds the SceneObjects and Viewports list.
     */
    constructor() {
        super();
        this.viewportIDCounter = 0;
        this.sceneObjects = [];
        this.viewports = [];
    }

    /**
     * When a SceneObject is created, it is routed through the SceneManager to the current
     * active scene to this function. The SceneObject will register with the scene to be referenced
     * on update/pause/destruct calls on a scene specific basis.
     * 
     * @param {SceneObject} sceneObject A SceneObject to register.
     */
    registerSceneObject(sceneObject) {
        this.sceneObjects.push(sceneObject);
        let deregisterCallback = () => {
            for (let i = 0; i < this.sceneObjects.length; i++) {
                if (this.sceneObjects[i].id ==sceneObject.id)
                    this.sceneObjects.splice(i, 1);
                    return;
            }
        }
        return deregisterCallback;
    }

    /**
     * Registers a Renderable component with the viewport, by index.
     * 
     * @param {Renderable} renderable A Renderable to register to the viewport.
     * @param {number} viewportIndex The index of the viewport.
     */
    registerRenderableComponent(renderable, viewportIndex) {
        if (this.viewports.length > viewportIndex)
            return this.viewports[viewportIndex].registerRenderableComponent(renderable);
        else {
            throw "This viewport doesn't exist on this scene!";
        }
    }

    /**
     * When a Viewport is created, it is routed through the SceneManager to the current
     * active scene to this function. The Viewport will register with the scene to be referenced
     * by the RenderManager and assigned to by Renderables.
     * 
     * @param {Viewport} viewport A Viewport to register.
     */
    registerViewport(viewport) {
        viewport.viewportIndex = this.viewportIDCounter++;
        this.viewports.push(viewport);
        let deregisterCallback = () => {
            for (let i = 0; i < this.viewports.length; i++) {
                if (this.viewports[i].viewportIndex === viewport.viewportIndex) {
                    this.viewports.splice(i, 1);
                    return;
                }
            }
        }
        return deregisterCallback;
    }
}

import { Messager } from "./Messager";

/**
 * GlobalMessager is a singleton reference to a Messager class.
 * 
 * Acts as an engine-wise messager that can be used for miscellaneous events.
 */
const GlobalMessager = new Messager();
export default GlobalMessager;

/**
 * Helper class for waiting for async code to complete, before continuing with code.
 * Allows multiple async loading of different types (prmoises/async-await functions) to be organized 
 * into one "Loader".
 */
export default class Loader {
    /**
     * Loader constructor. Requires a callback function; called when all loading elements are complete.
     * 
     * @param {function} loadCompleteCallback Callback function once loading is complete.
     */
    constructor(loadCompleteCallback) {
        this.objectsToLoad = 0;
        this.loadCount = 0;
        this.loadCountQueued = 0;
        this.started = false;

        this.loadCompleteCallback = loadCompleteCallback;
    }

    /**
     * Adds a Promise to the loader. Adding a promise wraps it allow the Loader to determine when loading is done.
     * The load complete callback is not triggered until start() is called.
     * 
     * @param {Promise} promise A pending promise.
     */
    load(promise) {
        this.objectsToLoad++;
        promise.then(() => {
            if (this.started)
                this.loadCount++;
            else
                this.loadCountQueued++;

            if (this.loadCount >= this.objectsToLoad)
                this.loadCompleteCallback();
        });
    }

    /**
     * Notifies the loader that you allow the loadCompleteCallback to be triggered once
     * all items are loaded. Items will be loading before start is called, the results are just queued
     * until start is called.
     */
    start() {
        if (this.started)
            return;

        this.started = true;
        this.loadCount += this.loadCountQueued;
        this.loadCountQueued = 0;

        if (this.loadCount >= this.objectsToLoad)
            this.loadCompleteCallback();
    }
}

/**
 * Converts degrees to radians.
