import {success, error, info} from './Log';
import TestManager from './TestManager';

export default class Test {
    constructor(name) {
        this.name = name;
        this.tests = [];
        TestManager.addTest(this);
    }

    run() {
        this.steps();
        let i = 0;
        info('[Running]: ' + this.name);
        while(i < this.tests.length) {
            let tStart = performance.now();
            let result = this.tests[i].test();
            if (result) {
                let tEnd = performance.now();
                success('\t' + this.tests[i].name + " Time: " + (tEnd - tStart));
                i++;
            } else {
                let tEnd = performance.now();
                error('\t' + this.tests[i].name + " Time: " + (tEnd - tStart));
                break;
            }
        }
        if (i >= this.tests.length) {
            success('[Success]: ' + this.name);
        } else {
            error('[Failed]: ' + this.name);
        }
    }

    steps() {
        throw 'Steps must be added to the test!';
    }

    addStep(testName, functionStep) {
        this.tests.push({
            name: testName,
            test: functionStep,
        });
    }
}

import log from '../helper/Log';
import Test from '../helper/Test';

/**
 * Example test. Runs a few simple assertions and chains them.
 * The third one fails on purpose.
 * @test 
 */
export default class ExampleTest extends Test {
    constructor() {
        super('Example Test'); 

        this.value = 4;
    }

    steps() {
        this.addStep('Simple Assert 1', () => {
            this.value += 100;
            return this.value < 200;
        });
        this.addStep('Simple Assert 2', () => {
            this.value += 100;
            return this.value < 222;
        });
        this.addStep('Simple Assert 3 (should fail)', () => {
            this.value += 100;
            return this.value < 222;
        });
    }
}
